            <form className={classes.form}> 
            <Input  label='Amount'
             input= {{
             id:'amount',
             type:'number',
             min:'1',
             max:'5',
             step:'1',
            defaultValues:'1' ,  }}/>
  
             <button className={classes.formbutton} onClick={ctx.addItemToCartHandler(items)} >+Add</button>
            </form>
        
    )
}
