        setAddToCartItems([...addToCartItems,item])
    }

    const deleteItemToCartHandler= (id) =>{
      const list = ctx.items.filter((item) =>
    
      item.id!==id
         
      )
      setCart(list)
    }
    


    const cartItems={
        items:addToCartItems,
        totalAmount:1,
        addItem:addItemToCartHandler,
        removeItem:deleteItemToCartHandler,
        message:'CART'
    }
